DROP TABLE IF EXISTS `goods`;
CREATE TABLE `goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品id',
  `name` varchar(100) CHARACTER SET utf8 DEFAULT NULL COMMENT '商品名称',
  `price` int(11) DEFAULT NULL COMMENT '价格',
  `pic` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '图片路径',
  `des` varchar(200) CHARACTER SET utf8 DEFAULT NULL COMMENT '简介',
  `content` longblob COMMENT '商品详情',
  `userid` int(11) DEFAULT NULL COMMENT '商品发布人',
  `createtime` datetime DEFAULT NULL COMMENT '发布时间',
  `updatetime` datetime DEFAULT NULL COMMENT '修改时间',
  `view` int(11) DEFAULT NULL COMMENT '商品表',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

